import React, { Component, Fragment } from "react";
import { Router, Switch, Route} from "react-router-dom";

import LoginPage from "./LoginPage";
import RegistrationPage from "./RegistrationPage";
import history from './history';
import InstaTwitter from './InstaTwitter';
import Menu from './Components/Menu'
import NewUser from './Components/NewUser'
import UserProfile from './Components/UserProfile'
import CreateEditProfile from './Components/CreateEditProfile'
import HomePage from './Components/HomePage'
import Followers from './Components/Followers'
import Followings from './Components/Followings'
import OtherUserProfile from './Components/OtherUserProfile'





export default class Routes extends Component {

    constructor(props) {
        super(props)

        this.state = {
            otherUserName:localStorage.getItem('otheUserName'),
            id:0,
            username:''

        }

    }
    loggedUserData=data=>{
        console.log('parent router-->',data.loggedUserData.id)
        this.setState({id:data.loggedUserData.id,username:data.loggedUserData.username})
    }


    components(){

        // const{path}=useRouteMatch();
        return (
            <Fragment>


                    <Menu loggedUserData={this.loggedUserData}/>
                            <Route path={`/NewUser/:id`} component={NewUser} />
                            <Route path={`/UserProfile/:id`} render={props=>(<UserProfile {...props} 
                                        loggedUserId={this.state.id}
                                        loggedUserName={this.state.username} />)} 
                            />
                            <Route path={`/CreateEditProfile`} component={CreateEditProfile} />
                            <Route path="/HomePage" component={HomePage} />
                            <Route path={`/Followers`} render={props=>(<Followers {...props}
                                        loggedUserName={this.state.username}
                            />)} />
                            <Route path={`/Followings`} component={Followings} />
                            <Route path={`/OtherUserProfile`} component={OtherUserProfile} />


            </Fragment>);
    }


    render() {
        return (
            <div>
            {/* <Menu /> */}
            <Router history={history}>
                <Switch>
                     <Route path="/" exact component={InstaTwitter} /> 
                    <Route path="/LoginPage" component={LoginPage} />
                    <Route path="/RegistrationPage" component={RegistrationPage} />
                    {this.components()}



                </Switch>
            </Router>
            </div>
        )
    }
}
