import React, { Component } from 'react'
import axios from 'axios'

export class RegistrationAPI extends Component {

    constructor(props) {
        super(props)

        this.state = {

            response:{}

        }

        console.log('api constructor')
    }



    componentDidMount(){

        console.log('props of api-->',this.props)
        const{firstname,lastname,username,email,password}=this.props.userDetails


        axios
      .post("http://127.0.0.1:8020/minitwitter/users/", {
        first_name: firstname,
        last_name: lastname,
        email: email,
        username: username,
        password: password,
      }) // link of backend api
      .then((response) => {
        console.log('registration response-->',response);

      })
      .catch((error) => {
        console.log(error.response["status"]);

        if (error.response["status"] === 400) {
          console.log(this.state);
          alert(`Username Already Exist! `);
        }
        if(error.response['status']  === 406){
          console.log(this.state)
          alert(`Special characters are not allowed! `);
        }
      });
    const status=200

    this.props.returnResponse(status)

    }

    render() {

        return (
            <div>
                <h1>api call</h1>
            </div>
        )
    }
}

export default RegistrationAPI
