import React, { Component } from 'react'

export class UserProfilePage extends Component {

    constructor(props) {
        super(props)

        const{firstname,lastname,username,bio,id}=this.props.userData

        this.state = {

            firstname:firstname,
            lastname:lastname,
            username:username,
            bio:bio,
            id:id

        }
        console.log('props of view-user profile-->',this.props)
    }
    render() {
        const { tweets, firstname, lastname, username, bio } = this.state;
        return (
          <div>
            <div className="Profile">
              <div id="upper-portion">
                {firstname} {lastname}
              </div>

              <i className="fa fa-user-alt"></i>
              <div id="full-name">
                {firstname} {lastname}
              </div>
              <div id="user-id">@{username}</div>
              <div id="user-id">{bio}</div>
              <button id="tweets-available">Tweets</button>
              <div className="tweets">
                <h4 id="tweets">
                  {tweets.map((tweet) => (
                    <h4 key={tweet.id}>
                      <div id="tweetuser">
                        <i class="fa fa-user-circle"></i>@{username}
                        <br />
                      </div>
                      <div id="user-tweet">
                        <div id="twitter">{tweet.content}</div>
                      </div>
                      {
                          this.props.loggedUserName === this.state.username ?
                          <button id="btn-delete"
                            onClick={()=>this.handleDelete(tweet.id)}>
                                <i className="far fa-trash-alt"></i>
                            </button>:
                            null
                      }

                    </h4>
                  ))}
                </h4>
              </div>
              {this.props.loggedUserName === this.state.username ? (
                <button id="edit" type="button" onClick={this.clickForEdit}>
                  Edit
                </button>
              ) : null}
              <button
                id="exitbtn"
                onClick={() => {
                  history.push("/minitwitter/timeline/");
                }}
              >
                Exit
              </button>
            </div>
          </div>
        );
      }
}

export default UserProfilePage
