            'Content-Type': 'application/json',
            Authorization: "token "+localStorage.getItem('token')
        }


        axios
        .get('http://127.0.0.1:8020/minitwitter/current_user/')
        .then( response=>{
                console.log('menu-->',response)
                this.props.getLoggedUser(response)


