import React, { Component } from 'react'
import axios from 'axios'

export class RegistrationAPI extends Component {

    constructor(props) {
        super(props)


        console.log(' registration api constructor')
    }



    componentDidMount(){

        console.log('props of api-->',this.props)
        const{firstname,lastname,username,email,password}=this.props.userDetails


        axios
      .post("http://127.0.0.1:8020/minitwitter/users/", {
        first_name: firstname,
        last_name: lastname,
        email: email,
        username: username,
        password: password,
      }) // link of backend api
      .then((response) => {
        console.log('registration response-->',response);
        this.props.returnResponse(response)

      })
      .catch((error) => {
        console.log(error.response["status"]);

        const errorCode=error.response["status"]
        this.props.returnErrorCode(errorCode)
      });




    }

}

export default RegistrationAPI
