import React, { Component } from 'react'
import UserProfilePage from './UserProfile.view'
import EditProfileAPI from './../../APIs/EditProfileAPI'

export class UserProfile extends Component {


    constructor(props) {
        super(props)

        this.state = {
             shouldCallAPI:false
        }
    }

    componentWillMount(){
        this.setState({shouldCallAPI:true})
    }

    setUserData=response=>{
        console.log('response from edit profile api-->',response)
        this.setState({
                  firstname: response.data.user["first_name"],
                  lastname: response.data.user["last_name"],
                  username: response.data.user["username"],
                  bio: response.data["bio"],
                  id: response.data.user["id"],
                })

    }

    render() {
        return (
            <div>
                <EditProfileAPI id={this.props.loggedUserID}

                />

            </div>
        )
    }
}

export default UserProfile
